  half = a_len / 2;
  background(220);
  let odd = false;
  let index = 0
  for (let x = 0; x < width; x += a_len) {
    for (let y = 0; y < height; y += a_len) {
      let odd = a_arr[index]
      if (odd) {
        drawLeft(x, y);
      } else {
        drawRight(x, y);
